<?php

namespace App\Libraries\SPPD;

use App\Libraries\PDF\PDFGenerator;
use App\Models\SPPD\SPPDModel;
use App\Models\SPPD\SPPDPegawaiModel;
use App\Models\User\UserModel;
use App\Models\Bidang\BidangModel;
use App\Models\Program\SubKegiatanModel;

/**
 * Surat Tugas Generator
 * 
 * Generates Surat Tugas PDF for SPPD
 */
class SuratTugasGenerator
{
    protected $sppdModel;
    protected $sppdPegawaiModel;
    protected $userModel;
    protected $subKegiatanModel;
    protected $bidangModel;
    protected $pdfGenerator;

    public function __construct()
    {
        $this->sppdModel = new SPPDModel();
        $this->sppdPegawaiModel = new SPPDPegawaiModel();
        $this->subKegiatanModel = new SubKegiatanModel();
        $this->userModel = new UserModel();
        $this->bidangModel = new BidangModel();
        $this->pdfGenerator = new PDFGenerator([
            'format' => 'A4',
            'margin_left' => 20,
            'margin_right' => 20,
            'margin_top' => 20,
            'margin_bottom' => 20,
        ]);
    }

    /**
     * Generate Surat Tugas PDF
     * 
     * @param int $sppdId
     * @param string $outputType 'I' = inline, 'D' = download, 'F' = save to file, 'S' = string
     * @param string|null $filename
     * @return mixed
     */
    public function generate(int $sppdId, string $outputType = 'I', ?string $filename = null)
    {
        $sppd = $this->sppdModel->getWithRelations($sppdId);
        
        if (!$sppd) {
            throw new \Exception('SPPD tidak ditemukan');
        }

        $pegawaiList = $this->sppdPegawaiModel->getPegawaiBySppdId($sppdId);
        $kadis = $this->userModel->where('role', 'kepaladinas')->first();
        $subKegiatan = $this->subKegiatanModel->getWithRelationss($sppd['sub_kegiatan_id']);
        // Generate HTML
        $html = $this->generateHTML($sppd, $pegawaiList, $kadis, $subKegiatan);

        // Set document properties
        $this->pdfGenerator
            ->setTitle('Surat Tugas - ' . ($sppd['no_sppd'] ?? 'Draft'))
            ->setAuthor('Aplikasi Perjadin')
            ->setSubject('Surat Tugas Perjalanan Dinas')
            ->generate($html);

        // Set default filename
        if (!$filename) {
            $noSppd = $sppd['no_sppd'] ?? 'DRAFT_' . $sppdId;
            $filename = 'Surat_Tugas_' . str_replace('/', '_', $noSppd) . '.pdf';
        }

        return $this->pdfGenerator->output($filename, $outputType);
    }

    /**
     * Generate HTML for Surat Tugas
     * 
     * @param array $sppd
     * @param array $pegawaiList
     * @return string
     */
    protected function generateHTML(array $sppd, array $pegawaiList, $kadis, $subKegiatan): string
    {
        $html = '
        <!DOCTYPE html>
        <html lang="en">
        <head>
            <meta charset="UTF-8">
            <meta name="viewport" content="width=device-width, initial-scale=1.0">
            <title>Surat Tugas</title>
            <style>
                body {
                    font-family: Arial, sans-serif;
                    margin: 40px;
                    font-size: 13px;
                }
                .kopsurat {
                    display: flex;
                    align-items: center;
                    border-bottom: 2px solid black;
                    padding-bottom: 10px;
                    margin-bottom: 20px;
                }
                .logo {
                    width: 100px;
                    height: auto;
                    margin-right: 20px;
                }
                .kop-text {
                    text-align: center;
                    flex-grow: 1;
                }
                .kop-text h1 {
                    font-size: 14px;
                    margin: 2px 0;
                    font-weight: bold;
                }
                .kop-text h2 {
                    font-size: 13px;
                    margin: 2px 0;
                    font-weight: bold;
                }
                .kop-text p {
                    font-size: 11px;
                    margin: 2px 0;
                }
                .row {
                    margin-bottom: 20px;
                }
                .title {
                    text-align: center;
                    font-size: 15px;
                    margin-bottom: 10px;
                }
                .title hr {
                    width: 120px;
                    margin-top: 1px;
                    margin-bottom: 1px;
                    border: 1px solid black;
                }
                .data {
                    margin-bottom: 10px;
                }
                .dasar {
                    display: flex;
                    margin-bottom: 10px;
                }
                .jdasar {
                    width: 100px;
                }
                .tdasar {
                    display: flex;
                    flex-direction: column;
                }
                .ldasar {
                    margin-bottom: 5px;
                }
                hr {
                    border: 0;
                    border-top: 1px solid black;
                    margin: 15px 0;
                }
                .isi {
                    text-align: justify;
                    margin-bottom: 30px;
                }
                .isi p {
                    margin-bottom: 10px;
                }
                .isi table {
                    width: 100%;
                    border-collapse: collapse;
                    margin-bottom: 20px;
                    border: 1px solid black;
                }
                .isi table th, .isi table td {
                    border: 1px solid black;
                    padding: 5px;
                    text-align: left;
                }
                .isi table th {
                    text-align: center;
                    font-weight: bold;
                }
                .ttd {
                    width: 300px;
                    float: right;
                    text-align: center;
                    margin-bottom: 50px;
                }
                .ttd p {
                    margin: 5px 0;
                }
                .disposisi {
                    clear: both;
                    margin-top: 50px;
                }
                .disposisi table {
                    width: 100%;
                    border-collapse: collapse;
                    margin-top: 10px;
                }
                .disposisi table th, .disposisi table td {
                    border: 1px solid black;
                    padding: 5px;
                    text-align: center;
                }
                .disposisi table td {
                    height: 80px;
                }
            </style>
        </head>
        <body>';

        // Kop Surat
        $html .= '
           <div class="kopsurat">
                <img src="data:image/png;base64,">
            </div>';

        // Title
        $html .= '
            <div class="row">
                <div class="title">
                    <strong>SURAT TUGAS</strong>
                    <hr>
                    Nomor: ' . htmlspecialchars($sppd['no_sppd'] ?? '-') . '
                </div>';

        // Dasar
        $dasarList = explode("\n", $sppd['dasar_surat'] ?? 'Peraturan Pemerintah Nomor 72 Tahun 2019');
        $html .= '
                <div class="data">
                    <div class="dasar">
                        <div class="jdasar">Dasar :</div>
                        <div class="tdasar">';
        
        $no = 1;
        foreach ($dasarList as $dasar) {
            if (trim($dasar)) {
                $html .= '
                            <div class="ldasar">' . $no++ . '. ' . htmlspecialchars(trim($dasar)) . '</div>';
            }
        }
        
        $html .= '
                        </div>
                    </div>
                </div>
                <hr>
            </div>';

        // Isi
        $html .= '
            <div class="isi">
                <p>Dengan Ini Menugaskan Kepada :</p>
                <table>
                    <thead>
                        <tr>
                            <th style="text-align: center; width: 30px;">NO</th>
                            <th style="text-align: center;">NAMA</th>
                            <th style="text-align: center;">NIP</th>
                            <th style="text-align: center;">JABATAN</th>
                        </tr>
                    </thead>
                    <tbody>';

        // Daftar Pegawai
        $no = 1;
        foreach ($pegawaiList as $pegawai) {
            $html .= '
                        <tr>
                            <td style="text-align: center;">' . $no++ . '</td>
                            <td>' . htmlspecialchars($pegawai['nama'] ?? '-') . '</td>
                            <td>' . htmlspecialchars($pegawai['nip_nik'] ?? '-') . '</td>
                            <td>' . htmlspecialchars($pegawai['jabatan'] ?? '-') . '</td>
                        </tr>';
        }

        $html .= '
                    </tbody>
                </table>
                
                <p>Maksud Surat Tugas :</p>
                <table>
                    <tr>
                        <td style="width: 20px; text-align: center;">1</td>
                        <td style="width: 150px;">Keperluan</td>
                        <td style="width: 10px; text-align: center;">:</td>
                        <td>' . nl2br(htmlspecialchars($sppd['maksud_perjalanan'] ?? '-')) . '</td>
                    </tr>
                    <tr>
                        <td style="width: 20px; text-align: center;">2</td>
                        <td style="width: 150px;">Tempat Tujuan</td>
                        <td style="width: 10px; text-align: center;">:</td>
                        <td>' . htmlspecialchars($sppd['tempat_tujuan'] ?? '-') . '</td>
                    </tr>
                    <tr>
                        <td style="width: 20px; text-align: center;">3</td>
                        <td style="width: 150px;">Berlaku Mulai Tanggal</td>
                        <td style="width: 10px; text-align: center;">:</td>
                        <td>' . $this->formatDate($sppd['tanggal_berangkat']) . ' s/d ' . $this->formatDate($sppd['tanggal_kembali']) . '</td>
                    </tr>
                    <tr>
                        <td style="width: 20px; text-align: center;">4</td>
                        <td style="width: 150px;">Alat Transportasi</td>
                        <td style="width: 10px; text-align: center;">:</td>
                        <td>' . htmlspecialchars($sppd['alat_angkut'] ?? '-') . '</td>
                    </tr>
                    <tr>
                        <td style="width: 20px; text-align: center;">5</td>
                        <td style="width: 150px;">Pembebanan Biaya ST</td>
                        <td style="width: 10px; text-align: center;">:</td>
                        <td>Tahun Anggaran '.date('Y').'</td>
                    </tr>
                    <tr>
                        <td style="width: 20px; text-align: center;"></td>
                        <td style="width: 150px;">a. SKPD</td>
                        <td style="width: 10px; text-align: center;">:</td>
                        <td> DPPA-SKPD DPMPTSP Kabupaten Tanah Bumbu TA' . date('Y') . '</td>
                    </tr>
                    <tr>
                        <td style="width: 20px; text-align: center;"></td>
                        <td style="width: 150px;">b. Sub Kegiatan</td>
                        <td style="width: 10px; text-align: center;">:</td>
                        <td>' . htmlspecialchars($subKegiatan['nama_sub_kegiatan'] ?? '-') . '</td>
                    </tr>
                    <tr>
                        <td style="width: 20px; text-align: center;"></td>
                        <td style="width: 150px;">c. Kode Rekening Belanja</td>
                        <td style="width: 10px; text-align: center;">:</td>
                        <td>' . htmlspecialchars($sppd['kode_rekening'] ?? '-') . '</td>
                    </tr>
                </table>
                
                <p style="text-indent: 40px;">Demikian Surat Tugas ini dibuat untuk dilaksanakan sebagaimana mestinya, dan setelah melaksanakan tugas, agar segera menyampaikan laporan hasil perjalanan dinas kepada yang memberi tugas.</p>
            </div>';

        // TTD
        $penanggungJawab = $kadis['nama'];
        $nipPenanggungJawab = $kadis['nip_nik'];
        $pangkatGolongan = $kadis['pangkat_golongan'];
        
        $html .= '
            <div class="ttd">
                <p>Kepala Dinas</p>
                <p style="height: 60px;"></p>
                <p><strong><u>' . htmlspecialchars($penanggungJawab) . '</u></strong></p>
                <p>' . htmlspecialchars($pangkatGolongan) . '</p>
                <p>NIP. ' . htmlspecialchars($nipPenanggungJawab) . '</p>
            </div>';

        
        $html .= '
        </body>
        </html>';

        return $html;
    }

    /**
     * Save to file
     * 
     * @param int $sppdId
     * @param string|null $directory
     * @return array
     */
    public function saveToFile(int $sppdId, ?string $directory = null): array
    {
        if (!$directory) {
            $directory = FCPATH . 'uploads/surat_tugas/';
        }

        if (!is_dir($directory)) {
            mkdir($directory, 0755, true);
        }

        $sppd = $this->sppdModel->find($sppdId);
        
        if (!$sppd) {
            throw new \Exception('SPPD tidak ditemukan');
        }

        $noSppd = $sppd['no_sppd'] ?? 'DRAFT_' . $sppdId;
        $filename = 'Surat_Tugas_' . str_replace('/', '_', $noSppd) . '.pdf';
        $filepath = $directory . $filename;

        $this->generate($sppdId, 'F', $filepath);

        return [
            'filename' => $filename,
            'filepath' => $filepath,
            'url' => base_url('uploads/surat_tugas/' . $filename),
            'size' => filesize($filepath)
        ];
    }

    /**
     * Format date helper
     */
    protected function formatDate($date): string
    {
        if (empty($date) || $date === '0000-00-00' || $date === '0000-00-00 00:00:00') {
            return '-';
        }

        $timestamp = strtotime($date);
        if ($timestamp === false) {
            return '-';
        }

        $months = [
            1 => 'Januari', 'Februari', 'Maret', 'April', 'Mei', 'Juni',
            'Juli', 'Agustus', 'September', 'Oktober', 'November', 'Desember'
        ];

        return date('d', $timestamp) . ' ' . 
               $months[(int)date('n', $timestamp)] . ' ' . 
               date('Y', $timestamp);
    }
}